@tailwind base;
@tailwind components;
@tailwind utilities;

@font-face {
  font-family: "marble";
  src: url("theme/webfonts/MarbleCrimsonRegular.woff") format('woff');
  font-display: optional;
}

/* TODO: add various weights */
@font-face {
  font-family: "Scandia";
  src: url("theme/webfonts/Scandia-Regular.woff") format('woff');
  font-weight: normal;
  font-style: normal;
  font-display: optional;
  text-rendering: optimizeLegibility;
}

@font-face {
  font-family: "Scandia";
  src: url("theme/webfonts/Scandia-Medium.woff") format('woff');
  font-weight: 500; 
  font-style: normal;
  font-display: optional;
}

@font-face {
  font-family: "Scandia";
  src: url("theme/webfonts/Scandia-Bold.woff") format('woff');
  font-weight: bold;
  font-style: normal;
  font-display: optional;
}

@layer base {
  html {
    @apply text-shovels-dark;
  }

  h1, h2, h3, h4, h5, h6, dt {
    color: #01654D;
    @apply font-medium;
  }

  dt {
    @apply leading-none;
  }

  body {
    background-color: white;
    /* font-size: 21px; */
  }

  /* below prevents the 'flash' on page load */
  [x-cloak] {
    display: none !important;
  }
} 

@layer components {
  .navbar_item {
    @apply inline-flex items-center space-x-1 text-base font-medium text-emerald-900 hover:text-emerald-700;
  }

  /* Blog post article styling */
  .article p {
    @apply mb-6;
  }

  .article h2 {
    @apply text-2xl font-semibold text-gray-900 mt-8 mb-4;
  }

  .article h3 {
    @apply text-xl font-semibold text-gray-900 mt-6 mb-3;
  }

  .article table {
    @apply table-auto w-full border-collapse border border-gray-300 shadow-sm rounded-lg overflow-hidden my-6;
  }

  .article table thead {
    @apply bg-gray-50;
  }

  .article table th {
    @apply border border-gray-300 px-4 py-3 text-left font-semibold text-gray-900 bg-gray-50;
  }

  .article table td {
    @apply border border-gray-300 px-4 py-3 text-gray-700;
  }

  .article table tbody tr:nth-child(even) {
    @apply bg-gray-50;
  }

