   122→<script type="text/javascript">
   123→  function filterComponent() {
   124→    const urlParams = new URLSearchParams(window.location.search);
   125→    const category = urlParams.get('category') || null;
   126→    return {
   127→      category,
   128→      searchQuery: urlParams.get('q') || '',
   129→      handleSearch() {
   130→        const params = new URLSearchParams(window.location.search);
   131→        if (this.searchQuery) {
   132→          params.set('q', this.searchQuery);
   133→        } else {
   134→          params.delete('q');
   135→        }
   136→        if (this.category) {
   137→          params.set('category', this.category);
   138→        }
   139→        window.location.search = params.toString();
   140→      },
   141→      isMatchingCategory(articleCategory) {
   142→        return !this.category || this.category === articleCategory;
   143→      },
   144→      isMatchingSearch(summary, title) {
   145→        if (!this.searchQuery) return true;
   146→        
   147→        const query = this.searchQuery.toLowerCase();
   148→        const normalizedSummary = (summary || '').toLowerCase();
   149→        const normalizedTitle = (title || '').toLowerCase();
   150→        
   151→        return normalizedSummary.includes(query) || normalizedTitle.includes(query);
   152→      }
   153→    }
   154→  }
   155→  // Apply background color for active category tag
   156→  document.addEventListener('DOMContentLoaded', function () {
   157→    const categoryLinks = document.querySelectorAll('.category-li');
   158→    const currentCategory = new URLSearchParams(window.location.search).get('category');
   159→    console.log(currentCategory)
   160→    categoryLinks.forEach(link => {
   161→      console.log(link)
   162→      if (link.getAttribute('data-category') === currentCategory || (!currentCategory && link.getAttribute('data-category') === 'all')) {
   163→        // Apply active background color
   164→        link.classList.add('bg-[#E9E1CE]', 'hover:bg-stone-100');
   165→      } else {
   166→        // Ensure non-active links have the default background color
   167→        link.classList.add('bg-[#E8BD51]', 'hover:bg-[#E9E1CE]');
   168→        link.classList.remove('bg-[#E9E1CE]', 'hover:bg-stone-100');
   169→      }
   170→    });
   171→  });
   172→</script>
