{% extends "base.html" %}
{% block html_lang %}{{ article.lang }}{% endblock %}

{% block title %}{{ SITENAME }} {{ article.title|striptags  }}{% endblock %}

{% block head %}
  {{ super() }}

  {% import 'translations.html' as translations with context %}
  {% if translations.entry_hreflang(article) %}
    {{ translations.entry_hreflang(article) }}
  {% endif %}

  {% if article.summary %}
    <meta name="description" content="{{ article.summary|striptags|e }}" />
  {% endif %}

  {% for tag in article.tags %}
    <meta name="tags" content="{{ tag|striptags|e }}" />
  {% endfor %}

  <!-- Force Open Graph image priority -->
  {% if article.open_graph_image or article.image %}
  <meta property="og:image" content="{% if article.open_graph_image %}{{ SITEURL }}{{ article.open_graph_image }}{% else %}{{ SITEURL }}{{ article.image }}{% endif %}" />
  <meta property="og:image:secure_url" content="{% if article.open_graph_image %}{{ SITEURL }}{{ article.open_graph_image }}{% else %}{{ SITEURL }}{{ article.image }}{% endif %}" />
  <meta property="og:image:type" content="image/jpeg" />
  <meta property="og:image:width" content="1200" />
  <meta property="og:image:height" content="630" />
  {% endif %}

{% endblock %}

{% block structured_data %}
  <script type="application/ld+json">
  {
    "@context": "https://schema.org",
    "@type": "Article",
    "headline": "{{ article.title|striptags|e }}",
    "url": "{{ SITEURL }}/{{ article.url }}",
    "datePublished": "{{ article.date.isoformat() }}",
    {% if article.modified %}
    "dateModified": "{{ article.modified.isoformat() }}",
    {% else %}
    "dateModified": "{{ article.date.isoformat() }}",
    {% endif %}
    "author": {
      "@type": "Person",
      "name": "{{ article.author }}"
    },
    "publisher": {
      "@type": "Organization",
      "name": "Shovels",
      "logo": {
        "@type": "ImageObject",
        "url": "https://www.shovels.ai/theme/images/shovels-social.png"
      }
    },
    {% if article.summary %}
    "description": "{{ article.summary|striptags|truncate(160)|e }}",
    {% endif %}
    "image": "{% if article.image %}{{ SITEURL }}{{ article.image }}{% else %}https://www.shovels.ai/theme/images/shovels-social.png{% endif %}",
    "mainEntityOfPage": {
      "@type": "WebPage",
      "@id": "{{ SITEURL }}/{{ article.url }}"
    }
  }
  </script>
{% endblock structured_data %}

{% block content %}

<div class="mx-auto max-w-[744px]">
  <!-- header -->
  <div class="my-16 px-6 lg:px-8">
    <div class="mx-auto text-base leading-5 text-gray-700">
      {% if article.category %}
      <div class="mb-10">
        <a href="/blog/?category={{article.category}}" class="relative z-10 rounded bg-[#E9E1CE] text-gray-900 hover:bg-stone-100 text-xs px-3 py-2">{{ article.category }}</a>
      </div>
      {% endif %}
